import React, { Component } from 'react';
import { addASong } from '../../store/reducers/songReducer';
import { connect } from 'react-redux';
import Spotify from './Spotify';

//connect certain compomnents with the firestore using the firestoreConnect and compose at the bottom of this file:
import { firestoreConnect } from 'react-redux-firebase';
import { compose } from 'redux';
import SpotifyWebApi from 'spotify-web-api-js';
var spotifyApi = new SpotifyWebApi();

export class AdminPage extends Component {
  constructor() {
    super();
    this.state = {
      title: '',
      artist: '',
      album: '',
      length: '',
      upvotes: 0,
      token: '',
    };

    this.handleChange = this.handelChange.bind(this);
    this.handleSubmit = this.handelSubmit.bind(this);
  }

  componentDidMount() {
    let _token = this.props.tokens[0];
    if (_token) {
      // Set token
      console.log('token:', _token.token);
      this.setState({
        token: _token.token,
      });
      spotifyApi.setAccessToken(_token);
    }
  }

  handelChange = evt => {
    this.setState({
      [evt.target.id]: evt.target.value,
    });
  };

  handelSubmit = evt => {
    evt.preventDefault();
    spotifyApi.getMyCurrentPlayingTrack(null, (err, data) => {
      // this.setState({
      //   item: data.item,
      //   is_playing: data.is_playing,
      //   progress_ms: data.progress_ms,
      // });
      console.log('Song from admin page on submit:', data);
    });
    this.props.addASong(this.state);
    this.setState({
      title: '',
      artist: '',
      album: '',
      length: '',
      upvotes: 0,
    });
  };

  render() {
    return (
      <div>
        <div className="container">
          <form onSubmit={this.handleSubmit} className="white">
            <h5 className="grey-text text-darken-3">Add a Song:</h5>
            <div className="input-field">
              <label htmlFor="title">Song Title</label>
              <input
                type="text"
                id="title"
                value={this.state.title}
                onChange={this.handleChange}
              />
            </div>
            <div className="input-field">
              <label htmlFor="artist">Artist</label>
              <input
                type="text"
                id="artist"
                value={this.state.artist}
                onChange={this.handleChange}
              />
            </div>
            <div className="input-field">
              <label htmlFor="album">Album</label>
              <input
                type="text"
                id="album"
                value={this.state.album}
                onChange={this.handleChange}
              />
            </div>
            <div className="input-field">
              <label htmlFor="length">Runtime</label>
              <input
                type="number"
                min="0"
                step=".01"
                id="length"
                value={this.state.length}
                onChange={this.handleChange}
              />
            </div>
            <div className="input-field">
              <button className="btn pink lighten-1 z-depth-0">Add Song</button>
            </div>
          </form>
        </div>
        <Spotify className="spotifyPlayer" />
      </div>
    );
  }
}

const mapDispatchToProps = dispatch => ({
  addASong: song => {
    dispatch(addASong(song));
  },
});

const mapStateToProps = state => {
  return {
    tokens: state.firestore.ordered.spotifyToken,
  };
};

export default compose(
  connect(
    mapStateToProps,
    mapDispatchToProps
  ),
  firestoreConnect([
    { collection: 'spotifyToken', orderBy: [['timestamp', 'desc']] },
  ])
)(AdminPage);
