import React, { Component } from 'react';
import SingleSong from './SingleSong';
import * as $ from 'jquery';
import { connect } from 'react-redux';
import Player from './Player';

//connect certain compomnents with the firestore using the firestoreConnect and compose at the bottom of this file:
import { firestoreConnect } from 'react-redux-firebase';
import { compose } from 'redux';

export class AllSongs extends Component {
  constructor() {
    super();
    this.state = {
      token: null,
      item: {
        album: {
          images: [{ url: '' }],
        },
        name: '',
        artists: [{ name: '' }],
        duration_ms: 0,
      },
      is_playing: 'Paused',
      progress_ms: 0,
    };
    this.getCurrentlyPlaying = this.getCurrentlyPlaying.bind(this);
  }

  componentDidMount() {
    let _token = this.props.tokens[0];
    // Set token
    if (_token) {
      // Set token
      this.setState({
        token: _token,
      });
      this.getCurrentlyPlaying(_token);
      // this.playThisSong(_token);
      // this.viewDevices(_token);
    }
  }

  getCurrentlyPlaying(token) {
    // Make a call using the token
    $.ajax({
      url: 'https://api.spotify.com/v1/me/player',
      type: 'GET',
      beforeSend: xhr => {
        xhr.setRequestHeader('Authorization', 'Bearer ' + token);
      },
      success: data => {
        this.setState({
          item: data.item,
          is_playing: data.is_playing,
          progress_ms: data.progress_ms,
        });
      },
    });
  }

  render() {
    let { songs } = this.props;
    let pageSongs = songs && [...songs];
    let orderedSongs =
      pageSongs && pageSongs.sort((a, b) => (a.upvotes > b.upvotes ? -1 : 1));
    return (
      <div className="allSongs container">
        <h3 className="center">All Songs</h3>
        <table className="container">
          <tbody>
            <tr>
              <th>Title</th>
              <th>Artist</th>
              <th>Album</th>
              <th>Run Time</th>
              <th>Votes</th>
            </tr>
            {songs &&
              orderedSongs.map(song => {
                return <SingleSong key={song.id} {...song} />;
              })}
          </tbody>
        </table>
        <div>
          {!this.state.token && <h4>No song is playing...</h4>}
          {this.state.token && (
            <div>
              <Player
                item={this.state.item}
                is_playing={this.state.is_playing}
                progress_ms={this.progress_ms}
              />
            </div>
          )}
        </div>
      </div>
    );
  }
}

// mapDispatch;
const mapStateToProps = state => {
  return {
    songs: state.firestore.ordered.Songs,
    tokens: state.firestore.ordered.spotifyToken,
  };
};

//use compose to connect connect and firestoreConnect together...
export default compose(
  connect(
    mapStateToProps,
    null
  ),
  firestoreConnect([
    { collection: 'Songs', orderBy: [['upvotes', 'desc']] },
    { collection: 'spotifyToken', orderBy: [['timestamp', 'desc']] },
  ])
)(AllSongs);
